/**
 * smarkup - A simple markup language for generating HTML from text.
 */

/**
 * Convert smarkup text to HTML.
 * @param {string} markupText - The smarkup text to convert.
 * @returns {string} The generated HTML.
 */
function smarkup(markupText) {
  // Split the input text into lines
  const lines = markupText.split('\n');

  // Initialize an empty HTML string
  let html = '';

  // Loop through each line and convert it to HTML
  for (const line of lines) {
    if (line.startsWith('# ')) {
      // Heading
      html += `<h1>${line.slice(2)}</h1>`;
    } else if (line.startsWith('**') && line.endsWith('**')) {
      // Bold text
      html += `<strong>${line.slice(2, -2)}</strong>`;
    } else if (line.startsWith('*') && line.endsWith('*')) {
      // Italic text
      html += `<em>${line.slice(1, -1)}</em>`;
    } else if (line.startsWith('- ')) {
      // Unordered list item
      html += `<li>${line.slice(2)}</li>`;
    } else if (/^\d+\. /.test(line)) {
      // Ordered list item
      html += `<li>${line.split('. ')[1]}</li>`;
    } else if (line.includes('[') && line.includes(']') && line.includes('(') && line.includes(')')) {
      // Link
      const [linkText, url] = line.match(/\[(.*?)\]\((.*?)\)/).slice(1, 3);
      html += `<a href="${url}">${linkText}</a>`;
    } else {
      // Plain text
      html += `<p>${line}</p>`;
    }
  }

  return html;
}

// Export the smarkup function
module.exports = smarkup;
