    execute: ({ project, file, body }) => {
      console.log(`Writing file '${file}' to project '${project}'`);
      console.log(body);
      // Implement file writing logic here
    }
  },
  {
    name: 'testProject',
    description: 'Runs tests for the current project',
    example: '/testProject(project:my-project)',
    validate: ({ project }) => typeof project === 'string' && project.length > 0,
    execute: ({ project }) => {
      console.log(`Running tests for project '${project}'`);
      // Implement testing logic here
    }
  }
];

function executor(availableCommands) {
  const commandMap = new Map(availableCommands.map(c => [c.name, c]));

  return {
    execute: directives => {
      for (const directive of directives) {
        const command = commandMap.get(directive.action);
        if (command) {
          if (command.validate(directive.attributes, directive.body)) {
            command.execute(directive.attributes, directive.body);
          } else {
            console.error(`Invalid attributes or body for directive '${directive.action}'`);
          }
        } else {
          console.error(`Unknown directive '${directive.action}'`);
        }
      }
    }
  };
}

