describe('smarkup.parse', () => {
  it('should parse simple directives', () => {
    const { parsed } = smarkup.parse(fixtures.simple);
    expect(parsed).to.deep.equal([
      { type: 'directive', name: 'hello', args: [] },
      { type: 'text', value: 'This is some text.' },
      { type: 'directive', name: 'world', args: [] }
    ]);
  });

  it('should parse directives with arguments', () => {
    const { parsed } = smarkup.parse(fixtures.withArgs);
    expect(parsed).to.deep.equal([
      { type: 'directive', name: 'cmd', args: ['arg1', 'arg2'] },
      { type: 'text', value: 'This directive has arguments.' }
    ]);
  });

  // Additional test cases...
});
} readProjectFileâš¡ï¸

