    }

    messages.push({ role: 'user', content: userInput });
  }
};

const promptUser = async (prompt) => {
  const { stdin, stdout } = process;
  stdout.write(prompt);
  return new Promise((resolve) => {
    stdin.once('data', (data) => {
      resolve(data.toString().trim());
    });
  });
