       break;
    }

    const match = imageMarkdown.match(/\!\[.*?\]\((.*?)\)/);
    if (match && match[1]) {
      const imagePath = match[1]; // This is the filename like 'dining-1.png'
      panels.push({ caption, imagePath });
    } else {
