            <div class="comic-panels">
                ${comicData.panels.map((panel, index) => `
                <div class="panel">
                    ${panel.imagePath ? `<img src="${imagePrefix}${panel.imagePath}" alt="${panel.caption}">` : '<p>Image missing</p>'}
