const PROVIDER_SYMBOL = Symbol('provider');

const sigil = (name) => {
  const sigil = {
    [PROVIDER_SYMBOL]: new Map(),
    as: (target) => ({
      like: (provider) => {
        sigil[PROVIDER_SYMBOL].set(target, provider);
      }
    }),
    using: (instance) => ({
      upon: (input) => {
        const provider = sigil[PROVIDER_SYMBOL].get(instance.constructor);
        return provider(input);
      }
    })
  };
  return sigil;
};

export default sigil;
} read‚ö°Ô∏è

