ü™Ñ‚ú® read(project:hierophant,file:hierophant.js) {
class Hierophant {
  constructor(options) {
    this.options = options;
    this.components = new Map();
  }

  define(name) {
    const sigil = Symbol(name);
    return {
      register: (name, component) => {
        this.components.set(`${sigil}:${name}`, component);
      },
      using: (instance) => ({
        upon: (input) => {
          const provider = this.components.get(`${sigil}:${instance.constructor.name}`);
          return provider(input);
        }
      })
    };
  }

  register(name, dependencies, factory) {
    this.components.set(name, (...args) => {
      const deps = dependencies.map(dep => this.resolve(dep));
      return factory(...deps, ...args);
    });
  }

  resolve(name, ...args) {
    const component = this.components.get(name);
    if (!component) {
      throw new Error(`Component '${name}' not registered.`);
    }
    return component(...args);
  }
}

export default (options) => new Hierophant(options);
