import { readFile } from 'node:fs/promises';
import express from 'express';
import path from 'node:path';

class Isohex {
  constructor(configPath = '.isohex.json') {
    this.configPath = configPath;
    this.config = null;
    this.app = express();
    this.port = 3000; // Default port
  }

  async loadConfig() {
    try {
      const configData = await readFile(this.configPath, 'utf8');
      this.config = JSON.parse(configData);

      // Configure port from config if specified
      if (this.config.port) {
        this.port = this.config.port;
      }
    } catch (error) {
      console.error('Could not load configuration:', error);
      // Fallback to default configuration
      this.config = {
        users: [],
        applications: []
      };
    }
  }

  setupRoutes() {
    // Home interface route
    this.app.get('/', (req, res) => {
      res.send(`
        <!DOCTYPE html>
        <html>
          <head>
            <title>Isohex</title>
          </head>
          <body>
            <header>Isohex Personal Web Environment</header>
            <main>
              <h1>Welcome to Your Personal Site</h1>
            </main>
            <footer>Â© Phantomaton Isohex</footer>
          </body>
        </html>
      `);
    });

    // Login route (stub)
    this.app.post('/login', (req, res) => {
      // TODO: Implement actual authentication
      res.json({ status: 'Not implemented' });
    });
  }

  async start() {
    await this.loadConfig();
    this.setupRoutes();

    this.app.listen(this.port, () => {
      console.log(`Isohex running on port ${this.port}`);
    });
  }
}

// Main execution
const isohex = new Isohex();
isohex.start().catch(console.error);

export default Isohex;
