import express from 'express';
import fs from 'node:fs/promises';
import path from 'node:path';

export default async configuration => {
  const router = express.Router();
  const routersPath = path.join(process.cwd(), 'routers');

  try {
    // Read all router files in the routers directory
    const routerFiles = await fs.readdir(routersPath);

    for (const file of routerFiles) {
      if (file.endsWith('.js')) {
        const routerModule = await import(`./routers/${file}`);

        // Assuming each router module exports a function that takes configuration
        if (typeof routerModule.default === 'function') {
          router.use(routerModule.default(configuration));
        }
      }
    }
  } catch (error) {
    console.error('Error loading routers:', error);
  }

  return router;
};
} writeâš¡ï¸

