import express from 'express';
import session from 'express-session';
import bodyParser from 'body-parser';

import applications from './routers/applications.js';
import { authMiddleware } from './routers/authentication.js';
import authentication from './routers/authentication.js';
import home from './routers/home.js';
import preferences from './routers/preferences.js';

export default configuration => {
  const router = express.Router();

  // Session middleware
  router.use(session({
    secret: 'isohex-secret-key', // TODO: Use secure random key
    resave: false,
    saveUninitialized: true,
    cookie: { secure: false } // Set to true in production with HTTPS
  }));

  // Body parsing middleware
  router.use(bodyParser.urlencoded({ extended: true }));
  router.use(bodyParser.json());

  // Authentication routes
  router.use(authentication(configuration));

  // Apply authentication middleware to protected routes
  router.use(authMiddleware);

  // Protected routes
  router.use('/', home(configuration));
  router.use('/applications', applications(configuration));
  router.use('/preferences', preferences(configuration));

  return router;
};
