import express from 'express';
import fs from 'node:fs/promises';
import path from 'node:path';
import { exec } from 'node:child_process';
import { promisify } from 'node:util';

const execAsync = promisify(exec);
const app = express();
const PORT = process.env.PORT || 3000;

// Middleware to parse JSON bodies
app.use(express.json());

// Helper to get absolute path
const getAbsolutePath = (relativePath) => {
  return path.join(process.env.HOME, relativePath);
};

// Read file
app.get('/*', async (req, res) => {
  const targetPath = getAbsolutePath(req.path);

  try {
    const stats = await fs.stat(targetPath);

    if (stats.isDirectory()) {
      const files = await fs.readdir(targetPath);
      res.json({
        type: 'directory',
        permissions: {
          user: { read: true, write: true, execute: true },
          group: { read: true, write: true, execute: true },
          other: { read: true, write: true, execute: true }
        },
        links: stats.nlink,
        user: stats.uid,
        group: stats.gid,
        size: stats.size,
        name: path.basename(targetPath),
        content: {
          type: 'directory',
          data: files
        }
      });
    } else {
      const content = await fs.readFile(targetPath, 'utf8');
      res.json({
        type: 'file',
        permissions: {
          user: { read: true, write: true, execute: false },
          group: { read: true, write: true, execute: false },
          other: { read: true, write: true, execute: false }
        },
        links: stats.nlink,
        user: stats.uid,
        group: stats.gid,
        size: stats.size,
        name: path.basename(targetPath),
        content: {
          type: 'text',
          data: content
        }
      });
    }
  } catch (error) {
    res.status(404).json({ error: 'File or directory not found' });
  }
});

// Write file or create directory
app.put('/*', async (req, res) => {
  const targetPath = getAbsolutePath(req.path);
  const { content } = req.body;

  try {
    if (content.type === 'directory') {
      await fs.mkdir(targetPath, { recursive: true });
    } else if (content.type === 'text') {
      await fs.writeFile(targetPath, content.data, 'utf8');
    }
    res.status(201).json({ status: 'Created successfully' });
  } catch (error) {
    res.status(500).json({ error: 'Could not create file or directory' });
  }
});

// Execute file or command
app.post('/*', async (req, res) => {
  const targetPath = getAbsolutePath(req.path);
  const { arguments: args, command } = req.query;

  try {
    let result;
    if (command) {
      // Execute command in directory
      result = await execAsync(command, { cwd: path.dirname(targetPath) });
    } else {
      // Execute file
      result = await execAsync(`${targetPath} ${args || ''}`, { cwd: path.dirname(targetPath) });
    }

    res.json({
      output: {
        type: 'text',
        data: result.stdout
      },
      error: result.stderr ? {
        code: 1,
        type: 'text',
        data: result.stderr
      } : null
    });
  } catch (error) {
    res.status(500).json({ 
      error: {
        code: error.code,
        type: 'text',
        data: error.message
      }
    });
  }
});

// Start server
app.listen(PORT, () => {
  console.log(`Userserver running on port ${PORT}`);
});

export default app;
} writeâš¡ï¸

