import fs from 'node:fs/promises';
import path from 'node:path';
import { exec } from 'node:child_process';
import { promisify } from 'node:util';

const execAsync = promisify(exec);

export class Path {
  constructor(requestPath) {
    this.relativePath = requestPath.replace(/^\//, '');
    this.absolutePath = path.join(process.env.HOME, this.relativePath);
  }

  async stat() {
    try {
      return await fs.stat(this.absolutePath);
    } catch (error) {
      throw new Error('File or directory not found');
    }
  }

  async read() {
    const stats = await this.stat();

    if (stats.isDirectory()) {
      const files = await fs.readdir(this.absolutePath);
      return {
        type: 'directory',
        permissions: this._getPermissions(),
        links: stats.nlink,
        user: stats.uid,
        group: stats.gid,
        size: stats.size,
        name: path.basename(this.absolutePath),
        content: {
          type: 'directory',
          data: files
        }
      };
    } else {
      const content = await fs.readFile(this.absolutePath, 'utf8');
      return {
        type: 'file',
        permissions: this._getPermissions(),
        links: stats.nlink,
        user: stats.uid,
        group: stats.gid,
        size: stats.size,
        name: path.basename(this.absolutePath),
        content: {
          type: 'text',
          data: content
        }
      };
    }
  }

  async write(content) {
    if (content.type === 'directory') {
      await fs.mkdir(this.absolutePath, { recursive: true });
    } else if (content.type === 'text') {
      await fs.writeFile(this.absolutePath, content.data, 'utf8');
    } else {
      throw new Error('Unsupported content type');
    }
  }

  async execute(args = '') {
    try {
      const result = await execAsync(`${this.absolutePath} ${args}`, { 
        cwd: path.dirname(this.absolutePath) 
      });

      return {
        output: {
          type: 'text',
          data: result.stdout
        },
        error: result.stderr ? {
          code: 1,
          type: 'text',
          data: result.stderr
        } : null
      };
    } catch (error) {
      return {
        error: {
          code: error.code,
          type: 'text',
          data: error.message
        }
      };
    }
  }

  async executeCommand(command) {
    try {
      const result = await execAsync(command, { 
        cwd: this.absolutePath 
      });

      return {
        output: {
          type: 'text',
          data: result.stdout
        },
        error: result.stderr ? {
          code: 1,
          type: 'text',
          data: result.stderr
        } : null
      };
    } catch (error) {
      return {
        error: {
          code: error.code,
          type: 'text',
          data: error.message
        }
      };
    }
  }

  _getPermissions() {
    return {
      user: { read: true, write: true, execute: true },
      group: { read: true, write: true, execute: true },
      other: { read: true, write: true, execute: true }
    };
  }
}
} writeâš¡ï¸

