// Phantomaton Persistence Provider
import plugins from 'phantomaton-plugins';

// Create a flexible persistence mechanism
const createPersistenceProvider = (config = {}) => {
  // Configurable storage mechanism
  const storage = new Map(config.initialData || []);

  return {
    // Core persistence methods
    save: (key, value) => {
      storage.set(key, value);
      return value;
    },
    load: (key, defaultValue) => storage.get(key) ?? defaultValue,
    delete: (key) => storage.delete(key),
    clear: () => storage.clear(),

    // Additional utility methods
    keys: () => Array.from(storage.keys()),
    values: () => Array.from(storage.values()),
    entries: () => Array.from(storage.entries())
  };
};

// Export as a plugin with optional composition
export default plugins.create({
  persistence: plugins.optional,
  storage: createPersistenceProvider
});
} writeâš¡ï¸

