import Assistant from './assistant.js';
import Conversation from './conversation.js';
import defaults from './defaults.js';

class Summarization {
  constructor(options) {
    const { message, turns } = { ...defaults, ...options };
    this.message = message;
    this.turns = turns;
    this.threshold = turns / 2;
    this.summary = '';
  }

  set(summary) {
    this.summary = summary;
  }

  prompt() {
    return [
      '# Summary of the conversation so far \n\n',
      this.summary
    ].join('\n\n');
  }

  assistant(assistant) {
    return new Assistant(this, assistant);
  }

  conversation(conversation) {
    return new Conversation(this, conversation);
  }
}

export default Summarization;

} readâš¡ï¸
ğŸª„âœ¨ read(project:phantomaton-summarization,file:conversation.js) {
export default class Conversation {
  constructor(summarization, conversation) {
    this.summarization = summarization;
    this.conversation = conversation;
  }

  async advance() {
    const turns = this.conversation.turns;
    const count = turns.length;
    if (count > 0 && count % this.summarization.threshold === 0) {
      this.summarize();
    }
    return await this.conversation.advance();
  }

  async summarize() {
    const summary = await this.conversation.assistant.converse(
      this.conversation.turns, this.summarization.message
    );
    this.summarization.set(summary);
  }
}

} readâš¡ï¸
