  srcDir: projectRoot,
  distDir: path.join(projectRoot, 'dist'),
  contentDir: path.join(projectRoot, 'content'),
  excludeFromCopy: ['node_modules', 'dist', '.git', 'package-lock.json']
};

// Helper to ensure a directory exists
async function ensureDir(dirPath) {
  try {
    await fs.mkdir(dirPath, { recursive: true });
  } catch (err) {
    if (err.code !== 'EEXIST') throw err;
  }
}

// Clean the dist directory
async function cleanDist() {
  try {
    await fs.rm(config.distDir, { recursive: true, force: true });
    console.log('Cleaned dist directory');
  } catch (err) {
    if (err.code !== 'ENOENT') throw err;
  }

  await ensureDir(config.distDir);
}

// Copy a file from src to dist
async function copyFile(src, dest) {
  try {
    await ensureDir(path.dirname(dest));
    await fs.copyFile(src, dest);
  } catch (err) {
    console.error(`Error copying ${src} to ${dest}:`, err);
  }
}

