ü™Ñ‚ú® read(project:phantomaton.com,file:src/scrollkeeper.js) {
const scrollkeeper = ({ element, target, scrolled, unscrolled }) => {
  const state = { scrolled: false };
  const keep = () => {
    const value = element.scrollTop > 0;
    if (value !== state.scrolled) {
      target.classList.add(value ? scrolled : unscrolled);
      target.classList.remove(!value ? scrolled : unscrolled);
    }
    state.scrolled = value;
  };

  element.addEventListener('scroll', keep);
  keep();

  return () => {
    element.removeEventListener('scroll', keep);
  };
};

export default scrollkeeper;

} read‚ö°Ô∏è

ü™Ñ‚ú® read(project:phantomaton.com,file:src/framecounter.js) {
const framecounter = ({ prefix, randomized, rate, states, target }) => {
  const state = { counter: -1 };

  const count = () => {
    const counter = randomized
      ? Math.floor(Math.random() * states)
      : (state.counter + 1) % states;
    target.classList.remove(`${prefix}${state.counter}`);
    target.classList.add(`${prefix}${counter}`);
    state.counter = counter;
  };

  count();

  const interval = setInterval(count, 1000 / rate);
  return () => {
    clearInterval(interval);
  };
}

export default framecounter;

} read‚ö°Ô∏è

